document.addEventListener('DOMContentLoaded', function () {
	if (
		0 !==
		document.getElementsByClassName('fiftyshadesfurniture-handheld-footer-bar')
			.length
	) {
		;[].forEach.call(
			document.querySelectorAll(
				'.fiftyshadesfurniture-handheld-footer-bar .search > a'
			),
			function (t) {
				t.addEventListener('click', function (e) {
					t.parentElement.classList.toggle('active'), e.preventDefault()
				})
			}
		)
		function t(t) {
			return function (e) {
				t && -1 !== e.target.tabIndex
					? document.body.classList.add('sf-input-focused')
					: document.body.classList.remove('sf-input-focused')
			}
		}
		var n = document.getElementsByClassName(
				'fiftyshadesfurniture-handheld-footer-bar'
			),
			o = document.forms
		if (n.length && o.length)
			for (let e = 0; e < o.length; e++)
				n[0].contains(o[e]) ||
					(o[e].addEventListener('focus', t(!0), !0),
					o[e].addEventListener('blur', t(!1), !0))
	}
})
